<?phprequire_once FILE_ACCESS_CORE_CODE.'/Framework/MVC_superClasses_Core/modelSuperClass_Core/modelSuperClass_Core.php';class model extends modelSuperClass_Core{	private $array_or_images;		public function getSliderImages()	{		$xml = simplexml_load_file('Data/1_Website/Content/Pages/primary/corporate/data.xml');				foreach ($xml->image as $image)		{			$this->array_or_images[] = '<img src="/Data/Images/'.preg_replace('/_thumb/i', '', $image->path).'" alt="'.$image->alternative_text.'" title="'.$image->caption.'" width="'.$image->width.'" height="'.$image->height.'" />';		}				return $this->array_or_images;	}
}?>