<?phprequire_once FILE_ACCESS_CORE_CODE.'/Framework/MVC_superClasses_Core/viewSuperClass_Core/viewSuperClass_Core.php';class view extends viewSuperClass_Core{	private $array_of_images;		public function setArrayofImages($images){		$this->array_of_images = $images;	}		public function getArrayofImages()	{		return $this->array_of_images;	}		public function displaySlider()	{		$this->getArrayofImages();		$this->getJSlider();				$content= $this->renderTemplate('Project/1_Website/Design/Pages/primary/corporate/templates/corporateSlider.phtml');		response::getInstance()->addContentToTree(array('SLIDER'=>$content));	}		public function getJSlider()	{		$content = $this->renderTemplate('Project/1_Website/Design/Main_Layout/plugins/JSlider/templates/nivoSlider_link.phtml');		response::getInstance()->addContentToStack('global_javascript_bottom',array('NIVO SCRIPT'=>$content));					$content = $this->renderTemplate('Project/1_Website/Design/Main_Layout/plugins/JSlider/templates/nivoSlider_css_link.phtml');		response::getInstance()->addContentToStack('global_css',array('NIVO CSS'=>$content));	}		public function getAboutTemplate()	{// 		$this->getJSlider();				$content = $this->renderTemplate('Project/1_Website/Design/Pages/primary/corporate/templates/css_links.phtml');		response::getInstance()->addContentToStack('local_css',array('CURRENT SECTION CSS'=>$content));					$content = $this->renderTemplate('Project/1_Website/Design/Pages/primary/corporate/templates/inpage_javascript.js');		response::getInstance()->addContentToStack('local_javascript_bottom',array('CURRENT SECTION CSS'=>$content));				$content= $this->renderTemplate('Project/1_Website/Design/Pages/primary/corporate/templates/about.phtml');		response::getInstance()->addContentToTree(array('MAIN_CONTENT'=>$content));	}}?>