<?phprequire_once FILE_ACCESS_CORE_CODE.'/Framework/MVC_superClasses_Core/viewSuperClass_Core/viewSuperClass_Core.php';class view extends viewSuperClass_Core{	private $array_of_images;		public function setArrayofImages($images){		$this->array_of_images = $images;	}		public function getArrayofImages()	{		return $this->array_of_images;	}		public function displayBanner()	{		$this->getArrayofImages();				$content= $this->renderTemplate('Project/1_Website/Design/Pages/primary/process/templates/banner.phtml');		response::getInstance()->addContentToTree(array('BANNER'=>$content));	}}?>