<?phprequire_once FILE_ACCESS_CORE_CODE.'/Framework/MVC_superClasses_Core/viewSuperClass_Core/viewSuperClass_Core.php';class view extends viewSuperClass_Core{		private $recaptcha;	private $status;		public function setRecaptcha($recaptcha_template)	{		$this->recaptcha_template = $recaptcha_template;	}		public function getRecaptcha()	{		return $this->recaptcha_template;	}	//-------------------------------------------------------------------------------------------------------------------------------------		public function setStatus($status)	{		$this->status = $status;	}		public function getStatus()	{		return $this->status;	}		//-------------------------------------------------------------------------------------------------------------------------------------		private function getGoolgeMapScript()	{		$content = $this->renderTemplate('Project/1_Website/Design/Pages/secondary/contact/templates/js_links.phtml');		response::getInstance()->addContentToStack('global_javascript_bottom',array('GOOGLEMAPSCRIPT'=>$content));	}		//-------------------------------------------------------------------------------------------------------------------------------------			public function displayMainTemplate()	{		$this->getGoolgeMapScript();		$this->getValidation();				$content = $this->renderTemplate('Project/1_Website/Design/Pages/secondary/contact/templates/css_links.phtml');		response::getInstance()->addContentToStack('local_css',array('INPAGECSS'=>$content));				$content = $this->renderTemplate("Project/1_Website/Design/Pages/secondary/contact/templates/page_template.phtml");		response::getInstance()->addContentToTree(array("MAIN_CONTENT" => $content));	}		public function getValidation()	{		$content = $this->renderTemplate('Project/1_Website/Design/Main_Layout/plugins/Validation/templates/validation_link.phtml');		response::getInstance()->addContentToStack('global_javascript_top',array('VALIDATION SCRIPT'=>$content));	}		public function displayChineseMainTemplate()	{		$this->getGoolgeMapScript();			$content = $this->renderTemplate('Project/1_Website/Design/Pages/secondary/contact/templates/css_links.phtml');		response::getInstance()->addContentToStack('local_css',array('INPAGECSS'=>$content));			$content = $this->renderTemplate("Project/1_Website/Design/Pages/secondary/contact/templates/page_template-chinese.phtml");		response::getInstance()->addContentToTree(array("MAIN_CONTENT" => $content));		}}?>